<script>
import TourProgramRouteModalLink from "./TourProgramRouteModalLink.vue";

export default {
  name: "TourProgramRouteModal",

  emits: ["close"],

  components: {
    TourProgramRouteModalLink,
  },

  props: {
    place: {
      type: Object,
      required: true,
    },
    image: {
      type: String,
      required: true,
    },
    description: {
      type: String,
      required: true,
    },
  },

  computed: {
    googleMapLink() {
      if (this.place.lat && this.place.lng) {
        return `https://www.google.com/maps/search/${this.place.lat},${this.place.lng}`;
      } else {
        return "";
      }
    },
    mapsMeLink() {
      if (this.place.lat && this.place.lng) {
        return `mapsme://route?sll=${this.place.lat},${this.place.lng}&saddr=Destination&dll=${this.place.lat},${this.place.lng}&daddr=Destionation&type=pedestrian`;
      } else {
        return "";
      }
    },
    phoneNumberLink() {
      if (this.place.phone_number) {
        return `tel:${this.place.phone_number}`;
      } else {
        return "";
      }
    },
    backgroundImage() {
      if (this.image) {
        return {
          backgroundImage: `url(${this.image})`,
        };
      } else {
        return null;
      }
    },
  },
};
</script>
